/**
 * Handles any user macro-like keys that must send multiple key strokes.
 */
bool process_record_user(uint16_t keycode, keyrecord_t * record) {
    switch(keycode) {
    case MACRO_PTR:
        if (record->event.pressed) {
            SEND_STRING("->");
        } else {
            // released
        }
        break;
    }
    return true;
}

